import { useState, useEffect } from 'react';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component';

import { GridList } from '@material-ui/core';

import Loader from './components/Loader';
import Header from './components/Header';
import Pin from './components/Pin';

function App() {
  const [pins, setPins] = useState([]);

  const getData = async () => {
    try {
      const { data } = await axios.get('/api/allpins');
      setPins([...pins, ...data]);
      console.log(data);
    } catch (error) {
      console.log('ERROR!', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Header />
      {/* <button onClick={getData}>press me</button> */}
      <InfiniteScroll
        dataLength={pins.length}
        next={getData}
        hasMore={true}
        loader={<Loader />}
      >
        <GridList>

        </GridList>
        {pins.map((pin) => (
          <Pin url={pin.images.orig.url} key={pin.id} />
        ))}
      </InfiniteScroll>
      {/* <img src="https://s-media-cache-ak0.pinimg.com/136x136/c1/e3/b7/c1e3b7b629b4d446b392ed623d0c0017.jpg"></img> */}
    </>
  );
}

// images.236x.url

export default App;
