import { useState } from 'react';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component';
import fs from ""

import Loader from './components/Loader';
import Header from './components/Header';

const data = fs.readFileSync('nyc_ttp_pins.json', { encoding: 'utf-8' });

function App() {
  const [pins, setPins] = useState([]);

  const getData = async () => {
    try {
      const { data } = await axios.get('/api/allpins');
      setPins(data)
      console.log(data);
    } catch (error) {
      console.log('ERROR!', error);
    }
  };

  return (
    <>
      <Header />
      <button onClick={getData}>press me</button>
      <InfiniteScroll
        dataLength={pins.length}
        next={getData}
        hasMore={true}
        loader={<Loader />}
        // endMessage={
        //   <p style={{ textAlign: 'center' }}>
        //     <b>Yay! You have seen it all</b>
        //   </p>
        // }
      >
        {pins.map(pin => (<img src={pin.images.orig.url} key={pin.id} alt=""></img>)
        )}
      </InfiniteScroll>
      {/* <img src="https://s-media-cache-ak0.pinimg.com/136x136/c1/e3/b7/c1e3b7b629b4d446b392ed623d0c0017.jpg"></img> */}
    </>
  );
}

// images.236x.url

export default App;
