import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

export default function HomePageFade() {
  const [previousYPosition, setPreviousYPosition] = useState(0);
  const [startFade, setStartFade] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const currentYposition = window.scrollY;
      const isScrollingUp = currentYposition < previousYPosition;

      setStartFade(isScrollingUp)
      setPreviousYPosition(currentYposition)
    };
    window.addEventListener('scroll', handleScroll, false);

    return () => {
      window.removeEventListener('scroll', handleScroll, false);
    };
  }, [previousYPosition]);

  return <motion.div className="fade-component"></motion.div>;
}
